package dev.shtanko.concurrency.coroutines

import dev.shtanko.concurrency.TestBase
import kotlin.system.measureTimeMillis
import kotlinx.coroutines.async
import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test

class FactorialTest : TestBase() {

    @Test
    fun `calculate factorial test`() = runTest {
        val time = measureTimeMillis {
            val f1 = async { calculateFactorial(3) }
            val f2 = async { calculateFactorial(4) }
            val f3 = async { calculateFactorial(10) }
            val f4 = async { calculateFactorial(20).toString() }
            val f5 = async { calculateFactorial(100).toString() }
            val f6 = async { calculateFactorial(1000).toString() }
            val f7 = async { calculateFactorial(5000).toString() }
            val f8 = async { calculateFactorial(10_000).toString() }
            val result1 = f1.await()
            val result2 = f2.await()
            val result3 = f3.await()
            val result4 = f4.await()
            val result5 = f5.await()
            val result6 = f6.await()
            val result7 = f7.await()
            val result8 = f8.await()
            assertThat(result1).isEqualTo(6)
            assertThat(result2).isEqualTo(24)
            assertThat(result3).isEqualTo(3628800)
            assertThat(result4).isEqualTo("2432902008176640000")
            assertThat(result5).isEqualTo("93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000")
            assertThat(result6).isEqualTo(
                "",
            )
            assertThat(result7).isEqualTo(
                "",
            )
            assertThat(result8).isEqualTo(
                "",
            )
        }
        println("Factorial computation time taken: $time milliseconds")
    }
}
